// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "@openzeppelin/contracts/token/ERC20/presets/ERC20PresetMinterPauser.sol";

/**
 * @title token
 */

contract firstToken is ERC20PresetMinterPauser {
	uint256[4] amountsToFee;
	uint256[4] percents;
	address feeReceiver;
	mapping(address => bool) public excludeFeeWallet;

	constructor(
		uint256[4] memory _amountsToFee,
		uint256[4] memory _percents,
		address _feeReceiver,
		uint256 supply
	) ERC20PresetMinterPauser("firstToken", "FIRST") {
		amountsToFee = organize(_amountsToFee);
		percents = organize(_percents);
		feeReceiver = _feeReceiver;
		excludeFeeWallet[msg.sender] = true;
		_mint(msg.sender, supply);
	}

	function howMuch(uint256 _amount) public view returns (uint256 _newAmount) {
		uint256 newpercent;
		bool find = true;
		for (uint256 i = 0; i < 4; i++) {
			if (amountsToFee[i] < _amount) continue;
			newpercent = percents[i];
			find = true;
		}
		if (find) {
			return _amount - (_amount * newpercent) / 100;
		} else {
			return 0;
		}
	}

	function setTaxFee(
		uint256[4] memory _amountsToFee,
		uint256[4] memory _percents
	) public onlyRole(DEFAULT_ADMIN_ROLE) {
		amountsToFee = organize(_amountsToFee);
		percents = organize(_percents);
	}

	function setFeeReceiver(address _feeReceiver)
		public
		onlyRole(DEFAULT_ADMIN_ROLE)
	{
		feeReceiver = _feeReceiver;
	}

	function setExcludeFeeWallets(
		address[] memory excludeFeeWallets,
		bool access
	) public onlyRole(DEFAULT_ADMIN_ROLE) {
		for (uint256 i = 0; i < excludeFeeWallets.length; i++) {
			excludeFeeWallet[excludeFeeWallets[i]] = access;
		}
	}

	function organize(uint256[4] memory array)
		public
		pure
		returns (uint256[4] memory)
	{
		uint256[4] memory arr = array;

		for (uint256 i = 0; i < 4 - 1; i++) {
			for (uint256 j = 0; j < 4 - i - 1; j++) {
				if (arr[j] > arr[j + 1]) {
					(arr[j], arr[j + 1]) = (arr[j + 1], arr[j]);
				}
			}
		}

		return arr;
	}

	function _transfer(
		address _from,
		address _to,
		uint256 _amount
	) internal override {
		if (excludeFeeWallet[msg.sender]) {
			super._transfer(_from, _to, _amount);
		} else {
			uint256 newAmount = howMuch(_amount);
			super._transfer(_from, feeReceiver, (_amount - newAmount));
			super._transfer(_from, _to, newAmount);
		}
	}
}

